sys.stdin = StringIO.StringIO("LLLR**")
def rec(lista,initial=1):
	if len(lista)<=0:
		return initial
	else:
		letter = lista[0]
		if letter == "R":
			res = pool.apply_async(rec, (lista[1:],initial,))  
			return 2*res.get(timeout=1)+1
			# return 2*rec(lista[1:],initial)+1
		elif letter == "L":
			res = pool.apply_async(rec, (lista[1:],initial,))  
			return 2*res.get(timeout=1)
			# return 2*rec(lista[1:],initial)
		elif letter =="*":
			res1 = pool.apply_async(rec, (lista[1:]+"R",initial,))  
			res2 = pool.apply_async(rec, (lista[1:]+"R",initial,))  
			res3 = pool.apply_async(rec, (lista[1:]+"R",initial,))  
			return res1.get(timeout=1) + res2.get(timeout=1) +res3.get(timeout=1)
			# return rec(lista[1:]+"R",initial)+rec(lista[1:]+"L",initial)+rec(lista[1:]+"P",initial)
		else:
			res = pool.apply_async(rec, (lista[1:],initial,))  
			return res.get(timeout=1)
			# return rec(lista[1:],initial)

def evaluateString(line,initial=1):
	for letter in line:
		if letter =="L":
			initial = initial*2
		elif letter =="R":
			initial = initial*2+1
	return initial

